import './Filter.css';
import { Layout } from 'antd';
import { Checkbox } from 'antd';
import { useState, useEffect } from 'react';

const CheckboxGroup = Checkbox.Group;

function Filter() {
    const { Sider, Content } = Layout;
    const [options, setOptions] = useState([]);
    const [checkedValues, setCheckedValues] = useState([]);

    useEffect(() => {
        fetch('https://your-api.com/data')
            .then((res) => res.json())
            .then((data) => {
                console.log('成功拿到資料:', data);
                const selectedNames = data
                    .filter((item) => checkedValues.includes(item.id))
                    .map((item) => item.name);
                setOptions(selectedNames);
            })
            .catch((err) => {
                console.error('錯誤:', err);
            });
    }, []);

	return (
		<div className="FilterArea">
			<div className="LocationArea">
                <Layout className="LocationArea">
                    <Sider width="5%" className="LocationArea LocationTitle">
                        <p>位置</p>
                    </Sider>
                    <Content className="LocationArea">
                        <CheckboxGroup
                            options={options}
                            value={checkedValues}
                            onChange={setCheckedValues}
                        />
                    </Content>
                </Layout>
			</div>
		</div>
	);
}

export default Filter;
