import { Layout, Card, Carousel } from 'antd';
import './HouseDesc.css'

function HouseDesc() {
    const { Sider, Content } = Layout;
    const contentStyle = {
        margin: 0,
        height: '160px',
        color: '#fff',
        lineHeight: '160px',
        textAlign: 'center',
        background: '#364d79',
    };

    return (
        <div className="HouseDescArea">
            <Card hoverable>
                <Layout className="">
                    <Sider width="30%" className="WhiteArea">
                        <Carousel arrows infinite={true}>
                            <div>
                                <img className='imgStyle' src="https://i.pinimg.com/originals/cc/50/63/cc506354d3bd16a508b013e1bc037a96.jpg" alt="" />
                            </div>
                            <div>
                                <img className='imgStyle' src="https://i.pinimg.com/originals/cc/50/63/cc506354d3bd16a508b013e1bc037a96.jpg" alt="" />
                            </div>
                        </Carousel>
                    </Sider>
                    <Content className="WhiteArea">
                        <p>右欄</p>
                    </Content>
                </Layout>
            </Card>
        </div>
    );
}

export default HouseDesc;
